mapper.select:
select id,advertiser_id,budget,currency_id,deliver_start_date,deliver_end_date,owner_sale,cooperate_sales,
		exist_msa,exist_service,status,progress,remark
from pms_business_opportunity where 1 = 1

	and status = #{status}


	order by ${sort}

select count(1) 
from pms_business_opportunity where 1 = 1

	and status = #{status}
