mapper.select:
select o.id,,c.name as advertiser
from sales_booking_production.business_opportunities o left join sales_booking_production.clients c on o.advertiser_id = c.id
where o.deleted_at is null and o.id=#{id}

select o.id,,c.name as advertiser
from sales_booking_production.business_opportunities o left join sales_booking_production.clients c on o.advertiser_id = c.id 
where o.deleted_at is null

	and o.id = #{fmt_number}


	and o.name like "%"#{name}"%"


	and c.name like "%"#{advertiser}"%"


	and o.status = #{status}


	order by ${sort}

select count(1) from sales_booking_production.business_opportunities o left join sales_booking_production.clients c on o.advertiser_id = c.id where o.deleted_at is null
and o.id = #{fmt_number}
and o.name like "%"#{name}"%"
and c.name like "%"#{advertiser}"%"
and o.status = #{status}
