mapper.select:
select o.id,o.advertiser_id,o.budget,o.currency_id,o.deliver_start_date,o.deliver_end_date,o.owner_sale,o.cooperate_sales, o.exist_msa,o.exist_service,o.status,o.progress,o.remark,o.deleted,c.name as advertiser from sales_booking_production.pms_business_opportunity o left join sales_booking_production.clients c on o.advertiser_id = c.id where o.deleted = 1
and c.name like "%"#{advertiser}"%"
order by ${sort}
