mapper.select:
        select id, 
		from client_contacts
		where is_delete = 1 and id = #{id}

mapper.update:
update client_contacts set 
	contact_person = #{contact_person}, phone = #{phone}, email = #{email}, position = #{position}, 
	address = #{address}, is_delete = #{is_delete}, updated_at = #{updated_at}
where id = #{id}
