mapper.select:
        select id, 
		from sales_booking_production.client_contacts
		where is_delete = 1 and id = #{id}

mapper.update:
update sales_booking_production.client_contacts set 
	contact_person = #{contact_person}, phone = #{phone}, email = #{email}, position = #{position}, 
	is_delete = #{is_delete}, updated_at = #{updated_at}
where id = #{id}

