mapper.select:
select id, user_id, parent_id, type, created_at, created_by
from data_sharing
where id = #{value}

select id, user_id, parent_id, type, created_at, created_by
from data_sharing
where user_id = #{value}

mapper.update:
 update data_sharing set 
 	user_id = #{user_id}, parent_id = #{parent_id}, type = #{type}, 
		created_at = #{created_at}, created_by = #{created_by}
	where id = #{id}
